import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ServiceService } from '../services/service.service';
import { Service } from '../models/service.model';

@Component({
  selector: 'app-prices',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './prices.component.html',
  styleUrls: ['./prices.component.css']
})
export class PricesComponent implements OnInit {
  services: Service[] = [];
  loading = true;
  error: string | null = null;

  constructor(private serviceService: ServiceService) {}

  ngOnInit(): void {
    this.loadServices();
  }

  loadServices(): void {
    this.loading = true;
    this.serviceService.getServices().subscribe({
      next: (data: Service[]) => {
        this.services = data;
        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Erreur lors du chargement des tarifs';
        this.loading = false;
        console.error('Error loading services:', err);
      }
    });
  }
}
<section id="tarifs" class="prices">
  <div class="container">
    <h2 class="section-title">Nos Tarifs</h2>
    
    <div *ngIf="loading" class="loading">
      <p>Chargement des tarifs...</p>
    </div>
    
    <div *ngIf="error" class="error">
      <p>{{ error }}</p>
    </div>
    
    <div *ngIf="!loading && !error" class="price-grid">
      <div *ngFor="let service of services" class="price-card">
        <h3>{{ service.name }}</h3>
        <div class="price">{{ service.price }}€</div>
        <ul>
          <li *ngFor="let feature of service.features">{{ feature }}</li>
        </ul>
      </div>
    </div>
  </div>
</section>
