import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AppointmentService } from '../services/appointment.service';
import { ServiceService } from '../services/service.service';
import { Appointment } from '../models/appointment.model';
import { Service } from '../models/service.model';

@Component({
  selector: 'app-contact',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {
  appointment: Appointment = {
    name: '',
    email: '',
    phone: '',
    serviceType: '',
    message: ''
  };
  
  services: Service[] = [];
  loading = false;
  submitted = false;
  error: string | null = null;
  success: string | null = null;

  constructor(
    private appointmentService: AppointmentService,
    private serviceService: ServiceService
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  loadServices(): void {
    this.serviceService.getServices().subscribe({
      next: (data: Service[]) => {
        this.services = data;
      },
      error: (err: any) => {
        console.error('Error loading services:', err);
      }
    });
  }

  onSubmit(): void {
    if (!this.validateForm()) {
      return;
    }

    this.loading = true;
    this.error = null;
    this.success = null;

    this.appointmentService.createAppointment(this.appointment).subscribe({
      next: (response: Appointment) => {
        this.success = 'Votre demande de rendez-vous a été envoyée avec succès! Nous vous contacterons rapidement.';
        this.submitted = true;
        this.loading = false;
        this.resetForm();
      },
      error: (err: any) => {
        this.error = 'Erreur lors de l\'envoi de votre demande. Veuillez réessayer.';
        this.loading = false;
        console.error('Error creating appointment:', err);
      }
    });
  }

  validateForm(): boolean {
    if (!this.appointment.name || !this.appointment.email || !this.appointment.phone || !this.appointment.serviceType) {
      this.error = 'Veuillez remplir tous les champs obligatoires';
      return false;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(this.appointment.email)) {
      this.error = 'Veuillez entrer une adresse email valide';
      return false;
    }

    const phoneRegex = /^[\d\s\+\-\(\)]+$/;
    if (!phoneRegex.test(this.appointment.phone)) {
      this.error = 'Veuillez entrer un numéro de téléphone valide';
      return false;
    }

    return true;
  }

  resetForm(): void {
    this.appointment = {
      name: '',
      email: '',
      phone: '',
      serviceType: '',
      message: ''
    };
  }
}
<section id="contact" class="contact">
  <div class="container">
    <h2 class="section-title">Contact & Rendez-vous</h2>
    <div class="contact-content">
      <div class="contact-info">
        <h3>Nos Coordonnées</h3>
        <div class="info-item">
          <strong>Adresse:</strong><br>
          123 Rue de la Beauté<br>
          75001 Paris
        </div>
        <div class="info-item">
          <strong>Téléphone:</strong><br>
          01 23 45 67 89
        </div>
        <div class="info-item">
          <strong>Horaires:</strong><br>
          Mardi - Samedi: 9h-19h<br>
          Dimanche: 9h-13h<br>
          Lundi: Fermé
        </div>
      </div>
      <div class="contact-form">
        <h3>Prendre Rendez-vous</h3>
        
        <div *ngIf="success" class="success-message">
          {{ success }}
        </div>
        
        <div *ngIf="error" class="error-message">
          {{ error }}
        </div>
        
        <form *ngIf="!submitted" (ngSubmit)="onSubmit()">
          <input 
            type="text" 
            placeholder="Votre nom" 
            [(ngModel)]="appointment.name" 
            name="name" 
            required
          >
          <input 
            type="email" 
            placeholder="Votre email" 
            [(ngModel)]="appointment.email" 
            name="email" 
            required
          >
          <input 
            type="tel" 
            placeholder="Votre téléphone" 
            [(ngModel)]="appointment.phone" 
            name="phone" 
            required
          >
          <select 
            [(ngModel)]="appointment.serviceType" 
            name="serviceType" 
            required
          >
            <option value="">Choisir un service</option>
            <option *ngFor="let service of services" [value]="service.name">
              {{ service.name }}
            </option>
          </select>
          <textarea 
            placeholder="Message optionnel" 
            rows="4" 
            [(ngModel)]="appointment.message" 
            name="message"
          ></textarea>
          <button 
            type="submit" 
            class="submit-btn" 
            [disabled]="loading"
          >
            {{ loading ? 'Envoi en cours...' : 'Envoyer la demande' }}
          </button>
        </form>
      </div>
    </div>
  </div>
</section>
